pragma solidity ^0.5.0;
/**
 * @title Hashed Timelock контракты.
 *
 * Protocol:
 *
 *  1) newContract(receiver, hashlock, timelock) - отправитель вызывает это, чтобы создать
 *      новый HTL-контракт и получить обратно 32 байта contract_id
 *  2) withdraw(contractId, preimage) - получатель, зная прообраз хэш блокировки
 *     может забрать средства
 *  3) refund() - когда временной замок пройдет и если никто не вывел средства, отправитель 
 *     или создатель HTL-контракта может вернуть себе 
*/

contract HashedTimelock {

    // Событие вызываемое при создании нового HTL-контракта
    event LogHTLCNew(
        bytes32 indexed contractId,
        address indexed sender,
        address indexed receiver,
        uint amount,
        bytes32 hashlock,
        uint timelock
    );

    // Событие вызываемое при выводе средств
    event LogHTLCWithdraw(bytes32 indexed contractId);

    // Событие вызываемое при возврате средств
    event LogHTLCRefund(bytes32 indexed contractId);

    // Основной HTL-контракт
    struct LockContract {
        address payable sender; // отправитель
        address payable receiver; // получатель
        uint amount; // поличество денег
        bytes32 hashlock; // sha-2 sha256 хэш замок
        uint timelock; // Время UNIX в секундах - контракт блокируется до этого времени
        bool withdrawn; // логиечское поле - были ли средства выведены
        bool refunded; // логиечское поле - были ли средства возвращены
        bytes32 preimage; // прообраз чего-то
    }

    modifier fundsSent() {
        require(msg.value > 0, "msg.value must be > 0");
        _;
    }

    modifier futureTimelock(uint _time) {
        // only requirement is the timelock time is after the last blocktime (now).
        // probably want something a bit further in the future then this.
        // but this is still a useful sanity check:
        require(_time > now, "timelock time must be in the future");
        _;
    }

// Проверка контракта на наличие
    modifier contractExists(bytes32 _contractId) {
        require(haveContract(_contractId), "contractId does not exist");
        _;
    }

// ХЗ
    modifier hashlockMatches(bytes32 _contractId, bytes32 _x) {
        require(
            contracts[_contractId].hashlock == sha256(abi.encodePacked(_x)),
            "hashlock hash does not match"
        );
        _;
    }

// Проверка, можно ли вывести средства
    modifier withdrawable(bytes32 _contractId) {
        require(contracts[_contractId].receiver == msg.sender, "withdrawable: not receiver");
        require(contracts[_contractId].withdrawn == false, "withdrawable: already withdrawn");
        require(contracts[_contractId].timelock > now, "withdrawable: timelock time must be in the future");
        _;
    }

// проврка , можно ли вернуть деньги
    modifier refundable(bytes32 _contractId) {
        require(contracts[_contractId].sender == msg.sender, "refundable: not sender");
        require(contracts[_contractId].refunded == false, "refundable: already refunded");
        require(contracts[_contractId].withdrawn == false, "refundable: already withdrawn");
        require(contracts[_contractId].timelock <= now, "refundable: timelock not yet passed");
        _;
    }

// список размещенных HTL-контрактов
    mapping (bytes32 => LockContract) contracts;

    /**
     * @dev Отправитель задает новый htl-контракт с депозитом в ETH и 
     * предоставляет получателю условия для блокировки.
     *
     * @param _receiver Получатель ETH.
     * @param _hashlock A sha-2 sha256 хэш замок
     * @param _timelock Эпоха UNIX в секундах, до которого средства блокируеются. Возврат сресдств может быть сделан после этого времени
     * @return contractId Id нововго контракта. Это необзодимос для последующих вызовов
     */
    function newContract(address payable _receiver, bytes32 _hashlock, uint _timelock)
        external
        payable
        fundsSent
        futureTimelock(_timelock)
        returns (bytes32 contractId)
    {
        contractId = sha256(
            abi.encodePacked(
                msg.sender,
                _receiver,
                msg.value,
                _hashlock,
                _timelock
            )
        );

        // отменить добавление если этот контракт уже существует с этими же параметрами
        // отправитель должен изменить одтн из параметров, чтобы создаиьб новый контарт
        if (haveContract(contractId))
            revert();

        contracts[contractId] = LockContract(
            msg.sender,
            _receiver,
            msg.value,
            _hashlock,
            _timelock,
            false,
            false,
            0x0
        );

        emit LogHTLCNew(
            contractId,
            msg.sender,
            _receiver,
            msg.value,
            _hashlock,
            _timelock
        );
    }

    /**
     * @dev Вызывается получателем, когда он узнает прообразо хэш замка. 
     * это отправит средства на его адресс
     *
     * @param _contractId Id HTLC.
     * @param _preimage sha256(_preimage) должен быть эквивантен хэш замку контпакта 
     * @return true есои всё норм
     */
    function withdraw(bytes32 _contractId, bytes32 _preimage)
        external
        contractExists(_contractId)
        hashlockMatches(_contractId, _preimage)
        withdrawable(_contractId)
        returns (bool)
    {
        LockContract storage c = contracts[_contractId];
        c.preimage = _preimage;
        c.withdrawn = true;
        c.receiver.transfer(c.amount);
        emit LogHTLCWithdraw(_contractId);
        return true;
    }

    /**
     * @dev Called by the sender if there was no withdraw AND the time lock has
     * expired. This will refund the contract amount.
     *
     * @param _contractId Id of HTLC to refund from.
     * @return bool true on success
     */
    function refund(bytes32 _contractId)
        external
        contractExists(_contractId)
        refundable(_contractId)
        returns (bool)
    {
        LockContract storage c = contracts[_contractId];
        c.refunded = true;
        c.sender.transfer(c.amount);
        emit LogHTLCRefund(_contractId);
        return true;
    }

    /**
     * @dev Get contract details.
     * @param _contractId HTLC contract id
     * @return All parameters in struct LockContract for _contractId HTLC
     */
    function getContract(bytes32 _contractId)
        public
        view
        returns (
            address sender,
            address receiver,
            uint amount,
            bytes32 hashlock,
            uint timelock,
            bool withdrawn,
            bool refunded,
            bytes32 preimage
        )
    {
        if (haveContract(_contractId) == false)
            return (address(0), address(0), 0, 0, 0, false, false, 0);
        LockContract storage c = contracts[_contractId];
        return (c.sender, c.receiver, c.amount, c.hashlock, c.timelock,
                c.withdrawn, c.refunded, c.preimage);
    }

    /**
     * @dev Is there a contract with id _contractId.
     * @param _contractId Id into contracts mapping.
     */
    function haveContract(bytes32 _contractId)
        internal
        view
        returns (bool exists)
    {
        exists = (contracts[_contractId].sender != address(0));
    }

}

